import React, { useState, useEffect } from 'react';
import './Chat.css';
import { Avatar, IconButton } from '@material-ui/core';
import AttachFileIcon from '@material-ui/icons/AttachFile';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import SearchIcon from '@material-ui/icons/Search';
import EmojiEmotionsIcon from '@material-ui/icons/EmojiEmotions';
import MicIcon from '@material-ui/icons/Mic';
import { useParams } from 'react-router-dom';
import db from '../../firebase';

function Chat() {

    const [input, setInput] = useState('');
    const [roomName, setRoomName] = useState('');
    const { roomId } = useParams();
    const [seed, setSeed] = useState('');
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        setSeed(Math.floor(Math.random() * 5000));
    }, [roomId])

    useEffect(() => {
        if (roomId) {
            db.collection('rooms').doc(roomId)
                .onSnapshot(
                    snapShot => (setRoomName(snapShot.data().name))
                )
            db.collection('rooms').doc(roomId).collection('messages')
                .orderBy('timestamp', 'asc').onSnapshot(
                    snapShot => (setMessages(snapShot.docs.map
                        (doc => doc.data())
                    )))
        }
    }, [roomId])

    const sendMessage = (e) => {
        e.preventDefault();
        //to sent message
        console.log("clicked => ", input)
        setInput('');
    }
    return (
        <div className="chat">
            <div className="chat__header">
                <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`} />
                <div className="chat__headerInfo">
                    <h3>{roomName}</h3>
                    <p>Last seen at ....</p>
                </div>
                <div className="chat_headerRight">
                    <IconButton>
                        <SearchIcon />
                    </IconButton>
                    <IconButton>
                        <AttachFileIcon />
                    </IconButton>
                    <IconButton>
                        <MoreVertIcon />
                    </IconButton>
                </div>
            </div>
            <div className="chat__body">
                {messages.map(message =>
                    (<p className={`chat__message ${true && `chat__reciever`}`}>
                        <span className="chat__name">{message.name}</span>
                            {message.message}
                        <span className="chat__timestamp">
                            {
                                new Date(message.timestamp?.toDate()).toLocaleTimeString()
                            }
                        </span>
                    </p>))}

            </div>
            <div className="chat__footer">
                <EmojiEmotionsIcon />
                <form>
                    <input onChange={(e) => setInput(e.target.value)} placeholder="Type a message" type="text" value={input} />
                    <button type="submit" onClick={sendMessage}>Sent Message</button>
                </form>
                <MicIcon />
            </div>
        </div>
    )
}

export default Chat
